import React from 'react';
import { TextField } from '@mui/material';

// Define an interface for the component props
interface QuoteInputProps {
    quote: string;
    setQuote: (quote: string) => void;
    handleSave: () => Promise<void>;
}

const QuoteInput: React.FC<QuoteInputProps> = ({ quote, setQuote, handleSave }) => (
    <>
      <TextField
    label="Add New Quote"
    variant="outlined"
    fullWidth
    value={quote}
    onChange={(e) => setQuote(e.target.value)}
    sx={{
        mb: 2,
        '& label': { // Style for the label
            color: 'green',
        },
        '& .MuiOutlinedInput-root': {
            '& fieldset': { // Style for the border
                borderColor: 'blue',
            },
            '&:hover fieldset': {
                borderColor: 'red', // Style for the border on hover
            },
            '&.Mui-focused fieldset': { // Style for the border when focused
                borderColor: 'purple',
            },
            'input': { // Style for the input text
                color: 'orange',
            },
        },
    }}
/>

    </>
);

export default QuoteInput;
